import{d as p}from"./app.147292e0.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="\u65E0\u8BBA\u4F60\u9047\u89C1\u8C01-\u4ED6\u90FD\u662F\u4F60\u751F\u547D\u8BE5\u51FA\u73B0\u7684\u4EBA-\u7EDD\u975E\u5076\u7136" tabindex="-1"><a class="header-anchor" href="#\u65E0\u8BBA\u4F60\u9047\u89C1\u8C01-\u4ED6\u90FD\u662F\u4F60\u751F\u547D\u8BE5\u51FA\u73B0\u7684\u4EBA-\u7EDD\u975E\u5076\u7136" aria-hidden="true">#</a> \u65E0\u8BBA\u4F60\u9047\u89C1\u8C01\uFF0C\u4ED6\u90FD\u662F\u4F60\u751F\u547D\u8BE5\u51FA\u73B0\u7684\u4EBA\uFF0C\u7EDD\u975E\u5076\u7136</h1><p>\u200B\u200B\u200B\u200B \u5F88\u9AD8\u5174\u9047\u89C1\u4F60\uFF0C\u5728\u4F60\u6211\u90FD\u9C9C\u6D3B\u7684\u5E74\u7EAA\u3002 \u200B\u200B\u200B\u200B</p><h2 id="_01" tabindex="-1"><a class="header-anchor" href="#_01" aria-hidden="true">#</a> 01</h2><p>\u6211\u4E00\u76F4\u5F88\u76F8\u4FE1\u4E00\u53E5\u8BDD\uFF1A\u65E0\u8BBA\u4F60\u9047\u89C1\u8C01\uFF0C\u4ED6\u90FD\u662F\u4F60\u751F\u547D\u4E2D\u8BE5\u51FA\u73B0\u7684\u4EBA\uFF0C\u7EDD\u975E\u5076\u7136\uFF0C\u4ED6\u4E00\u5B9A\u4F1A\u6559\u4F1A\u4F60\u4E00\u4E9B\u4EC0\u4E48\u3002</p><p>\u4E07\u5343\u4E16\u754C\uFF0C\u832B\u832B\u4EBA\u6D77\uFF0C\u65E0\u8BBA\u4F60\u8D70\u5230\u54EA\u91CC\uFF0C\u7ECF\u5386\u4EC0\u4E48\u6837\u7684\u7ECF\u5386\uFF0C\u9047\u89C1\u4EC0\u4E48\u6837\u7684\u4EBA\uFF0C\u53D1\u751F\u600E\u6837\u7684\u6545\u4E8B\uFF0C\u4E00\u5207\u90FD\u662F\u8BE5\u53D1\u751F\u7684\uFF0C\u662F\u4F60\u8BE5\u8DEF\u8FC7\u7684\u98CE\u666F\uFF0C\u4E5F\u662F\u4F60\u8BE5\u5230\u8FBE\u7684\u8FDC\u65B9\u3002</p><p>\u8FD9\u4E00\u573A\u65C5\u7A0B\u4F1A\u8BA9\u4F60\u6709\u6240\u5931\uFF0C\u4E5F\u4F1A\u8BA9\u4F60\u6709\u6240\u5F97\uFF0C\u4F46\u5B83\u4EEC\u90FD\u5728\u5851\u9020\u4E00\u4E2A\u66F4\u65B0\u7684\u4F60\u3002</p><p>\u4EBA\u4E00\u751F\u4F1A\u9047\u5230\u7EA6 2920 \u4E07\u4EBA\uFF0C\u4E24\u4E2A\u4EBA\u76F8\u8BC6\u7684\u6982\u7387\u53EA\u6709\u5343\u4E07\u5206\u4E4B\u4E94\uFF0C\u76F8\u77E5\u7684\u6982\u7387\u53EA\u6709\u5341\u4EBF\u5206\u4E4B\u4E09\uFF0C\u8FD9\u4E48\u5FAE\u4E4E\u5176\u5FAE\u7684\u51E0\u7387\uFF0C\u5374\u6BCF\u5929\u90FD\u5728\u6211\u4EEC\u7684\u751F\u6D3B\u4E2D\u4E0A\u6F14\u3002</p><p>\u54EA\u6709\u4EC0\u4E48\u5076\u7136\uFF0C\u4E16\u95F4\u6240\u6709\u7684\u76F8\u9047\uFF0C\u7686\u6709\u56E0\u7531\u3002</p><p>\u5C31\u50CF\u4F5B\u5BB6\u8BB2\uFF0C\u82E5\u65E0\u76F8\u6B20\uFF0C\u600E\u4F1A\u76F8\u89C1\u3002</p><p>\u65E2\u7136\u5982\u6B64\uFF0C\u90A3\u4FBF\u6000\u4E00\u9897\u5E73\u5E38\u5766\u7136\u4E4B\u5FC3\u53BB\u5BF9\u5F85\u53D1\u751F\u7684\u4E00\u5207\uFF0C\u62E5\u6709\u7684\u65F6\u5019\u597D\u597D\u73CD\u60DC\uFF0C\u5230\u4E86\u544A\u522B\u7684\u65F6\u5019\u4FBF\u8BA4\u771F\u5730\u9053\u58F0\u518D\u89C1\u3002</p><h2 id="_02" tabindex="-1"><a class="header-anchor" href="#_02" aria-hidden="true">#</a> 02</h2><p>\u66FE\u542C\u4EBA\u8BB2\uFF0C\u7231\u4E0A\u4E00\u4E2A\u4E0D\u8BE5\u7231\u7684\u4EBA\u662F\u4EC0\u4E48\u611F\u89C9\uFF1F</p><p>\u5927\u6982\u5C31\u662F\uFF0C\u8FDB\u4E00\u6B65\u6CA1\u8D44\u683C\uFF0C\u9000\u4E00\u6B65\u820D\u4E0D\u5F97\uFF0C\u8FDE\u5BF9\u65B9\u7684\u559C\u6012\u54C0\u4E50\u90FD\u8981\u4ECE\u65C1\u4EBA\u53E3\u4E2D\u542C\u8BF4\u3002</p><p>\u670B\u53CB\u4F83\u4F83\u5C31\u66FE\u7ECF\u5386\u8FC7\u8FD9\u6837\u4E00\u6BB5\u65E0\u75BE\u800C\u7EC8\u7684\u611F\u89C9\uFF0C\u7231\u8FC7\u75DB\u8FC7\uFF0C\u54ED\u8FC7\u7B11\u8FC7\uFF0C\u6700\u540E\u6CA1\u7ED3\u679C\u3002</p><p>\u5F88\u4E45\u5F88\u4E45\u4E4B\u540E\u518D\u63D0\u8D77\u65E7\u4EBA\uFF0C\u4F83\u4F83\u8BF4\uFF1A\u201C\u5176\u5B9E\u65F6\u95F4\u4E45\u4E86\u4E5F\u5C31\u91CA\u6000\u4E86\uFF0C\u53EF\u80FD\u662F\u54EA\u8F88\u5B50\u6B20\u4E86\u4ED6\u7684\uFF0C\u8981\u8FD9\u8F88\u5B50\u6765\u8FD8\uFF0C\u5C31\u5F53\u662F\u60C5\u503A\u4E00\u573A\uFF0C\u73B0\u5728\u770B\u5F00\u4E86\u5C31\u597D\u4E86\u3002\u201D</p><p>\u5982\u679C\u4F60\u8FD8\u6CA1\u6709\u653E\u4E0B\u90A3\u4E2A\u5DF2\u7ECF\u6210\u4E3A\u8FC7\u53BB\u5F0F\u7684\u4EBA\uFF0C\u90A3\u6211\u7ED9\u4F60\u8BB2\u4E2A\u6545\u4E8B\u5427\u3002</p><p>\u6709\u4F4D\u4E66\u751F\uFF0C\u81EA\u548C\u59D1\u5A18\u5B9A\u4EB2\u4EE5\u6765\uFF0C\u5173\u7CFB\u4E00\u76F4\u5F88\u597D\uFF0C\u4E66\u751F\u4EE5\u4E3A\u4ED6\u4EEC\u4F1A\u4E3E\u6848\u9F50\u7709\u767D\u5934\u5055\u8001\uFF0C\u53EF\u672A\u7B49\u6210\u4EB2\uFF0C\u59D1\u5A18\u5374\u79BB\u4ED6\u800C\u53BB\uFF0C\u7231\u4E0A\u4E86\u53E6\u5916\u4E00\u4E2A\u7537\u4EBA\u3002</p><p>\u4E66\u751F\u5F88\u6C14\u6124\uFF0C\u53BB\u95EE\u9AD8\u50E7\uFF1A\u660E\u660E\u6211\u4EEC\u5F7C\u6B64\u76F8\u7231\uFF0C\u4E3A\u4EC0\u4E48\u5979\u5374\u8981\u9009\u62E9\u522B\u4EBA\uFF1F</p><p>\u9AD8\u50E7\u8BF4\uFF1A\u4E00\u5207\u7686\u6709\u56E0\u679C\uFF0C\u4E00\u5207\u90FD\u662F\u5BBF\u547D\u3002</p><p>\u4E66\u751F\u4E0D\u660E\u6240\u4EE5\uFF0C\u9AD8\u50E7\u53C8\u7ED9\u4ED6\u8BB2\u4E86\u4E2A\u5C0F\u6545\u4E8B\u3002</p><p>\u5F88\u4E45\u4EE5\u524D\uFF0C\u6709\u4F4D\u5973\u5B50\u5931\u8DB3\u843D\u6C34\uFF0C\u5C38\u4F53\u6F02\u6D41\u4E86\u5F88\u4E45\u624D\u88AB\u51B2\u5230\u4E00\u5904\u504F\u50FB\u8352\u51C9\u7684\u5CB8\u4E0A\uFF0C\u5979\u8863\u4E0D\u853D\u4F53\u51E0\u4E4E\u5168\u8EAB\u8D64\u88F8\uFF0C\u5728\u6C34\u4E2D\u6CE1\u4E45\u4E86\u6709\u4E9B\u6D6E\u80BF\uFF0C\u770B\u4E0A\u53BB\u6709\u4E9B\u5413\u4EBA\u3002</p><p>\u6709\u4F4D\u884C\u4EBA\u8DEF\u8FC7\u8FD9\u91CC\uFF0C\u53EA\u770B\u4E86\u4E00\u773C\u4FBF\u5306\u5306\u79BB\u5F00\u4E86\uFF0C\u4E4B\u540E\u7B2C\u4E8C\u4F4D\u884C\u4EBA\u8DEF\u8FC7\uFF0C\u60F3\u4E86\u60F3\uFF0C\u628A\u81EA\u5DF1\u7684\u8863\u888D\u8131\u4E0B\u6765\u8986\u76D6\u5728\u5973\u5B50\u8EAB\u4E0A\uFF0C\u4E4B\u540E\u4E5F\u79BB\u5F00\u4E86\u3002</p><p>\u7B2C\u4E09\u4F4D\u884C\u4EBA\u770B\u5230\u5973\u5B50\uFF0C\u5FC3\u751F\u4E0D\u5FCD\uFF0C\u4FBF\u5C06\u5979\u597D\u751F\u5B89\u846C\u540E\u65B9\u624D\u79BB\u53BB\u3002</p><p>\u9AD8\u50E7\u8BF4\uFF1A\u8FD9\u5973\u5B50\u4FBF\u662F\u59D1\u5A18\u7684\u524D\u4E16\uFF0C\u5979\u7684\u524D\u4E16\u9047\u5230\u4E09\u4E2A\u7537\u4EBA\uFF0C\u4F60\u662F\u90A3\u4E2A\u8131\u4E0B\u8863\u888D\u4E3A\u5979\u76D6\u4F4F\u8EAB\u4F53\u7684\u4EBA\uFF0C\u4ECA\u751F\u76F8\u604B\u4E00\u573A\uFF0C\u5979\u6765\u8FD8\u4F60\u7684\u6069\u60C5\uFF0C\u4F46\u5979\u8981\u62A5\u7B54\u4E00\u751F\u4E00\u4E16\u7684\uFF0C\u662F\u90A3\u4E2A\u5C06\u5979\u57CB\u846C\u7684\u4EBA\u554A\u3002</p><p>\u4E66\u751F\u4E86\u7136\uFF0C\u653E\u4E0B\u4E86\u5FC3\u4E2D\u6267\u5FF5\u3002</p><p>\u6545\u4E8B\u8BB2\u5B8C\u4E86\uFF0C\u4E0D\u77E5\u9053\u4F60\u5FC3\u91CC\u662F\u5426\u4E5F\u60F3\u5230\u4E86\u51E0\u4E2A\u4EBA\uFF0C\u4E5F\u8BB8\u662F\u64E6\u80A9\u800C\u8FC7\u7684\u8DEF\u4EBA\uFF0C\u4E5F\u8BB8\u662F\u7231\u8FC7\u7684\u65E7\u4EBA\uFF0C\u4E5F\u8BB8\u662F\u65E5\u65E5\u76F8\u5BF9\u7684\u6795\u8FB9\u4EBA\u3002</p><p>\u56E0\u4E3A\u76F8\u6B20\uFF0C\u624D\u4F1A\u9047\u89C1\uFF0C\u8FD9\u4E00\u573A\u56E0\u7F18\u7ED3\u679C\uFF0C\u90FD\u662F\u5FC5\u7136\u3002</p><h2 id="_03" tabindex="-1"><a class="header-anchor" href="#_03" aria-hidden="true">#</a> 03</h2><p>\u4EBA\u8FD9\u4E00\u751F\u515C\u515C\u8F6C\u8F6C\uFF0C\u90FD\u5728\u627E\u5BFB\u81EA\u5DF1\u7684\u53E6\u4E00\u534A\u3002</p><p>\u4F46\u5C31\u50CF\u6545\u4E8B\u91CC\u7684\u90A3\u4E2A\u5973\u5B50\uFF0C\u8C01\u90FD\u6CA1\u6709\u9884\u77E5\u529F\u80FD\uFF0C\u53EA\u6709\u5F53\u90A3\u4E2A\u5BF9\u7684\u4EBA\u5728\u5BF9\u7684\u65F6\u95F4\u51FA\u73B0\u5728\u4F60\u9762\u524D\uFF0C\u4F60\u624D\u4F1A\u660E\u767D\u5F20\u7231\u73B2\u8BF4\u7684\u90A3\u79CD\u611F\u89C9\uFF1A</p><p>\u4E8E\u5343\u4E07\u4EBA\u4E4B\u4E2D\u9047\u89C1\u4F60\u6240\u9047\u89C1\u7684\u4EBA\uFF0C\u4E8E\u5343\u4E07\u5E74\u4E4B\u4E2D\u65F6\u95F4\u7684\u65E0\u6DAF\u7684\u8352\u91CE\u91CC\uFF0C\u6CA1\u6709\u65E9\u4E00\u6B65\uFF0C\u4E5F\u6CA1\u6709\u665A\u4E00\u6B65\uFF0C\u6B63\u5DE7\u8D76\u4E0A\u4E86\uFF0C\u90A3\u4E5F\u6CA1\u6709\u4EC0\u4E48\u522B\u7684\u53EF\u8BF4\uFF0C\u552F\u6709\u8F7B\u8F7B\u95EE\u4E00\u53E5\uFF1A\u201C\u54E6\uFF0C\u4F60\u4E5F\u5728\u8FD9\u91CC\u5417\uFF1F\u201D</p><p>\u6240\u4EE5\uFF0C\u6CA1\u6709\u4EC0\u4E48\u53EF\u9057\u61BE\u61CA\u607C\u7684\uFF0C\u9047\u89C1\u4E00\u573A\uFF0C\u5DF2\u7136\u662F\u83AB\u5927\u7684\u7F18\u5206\uFF0C\u7231\u5BF9\u4E86\u662F\u5E78\u8FD0\uFF0C\u7231\u9519\u4E86\uFF0C\u53C8\u4F55\u5C1D\u4E0D\u662F\u4E00\u79CD\u5B89\u6392\u3002</p><p>\u751F\u6D3B\u4E2D\u7684\u4E8B\u60C5\u540C\u6837\u662F\u8FD9\u6837\uFF0C\u6B22\u559C\u662F\u6070\u5982\u5176\u5206\uFF0C\u60B2\u4F24\u96BE\u8FC7\u540C\u6837\u4E5F\u6709\u5B83\u7684\u9053\u7406\u3002</p><p>\u56E0\u4E3A\u76F8\u6B20\uFF0C\u624D\u4F1A\u9047\u89C1\u3002\u6B20\u7684\u591A\u4E86\uFF0C\u8FD9\u4E00\u4E16\u4FBF\u7ED3\u4F34\u5730\u4E45\u4E00\u4E9B\uFF0C\u6B20\u7684\u5C11\u4E86\uFF0C\u8FD8\u5B8C\u4E86\u4E5F\u5C31\u6563\u4E86\u3002</p><p>\u5C31\u7B97\u771F\u7684\u6709\u4E0B\u4E00\u4E16\u8F6E\u56DE\uFF0C\u6211\u4EEC\u4E5F\u4E0D\u4F1A\u518D\u8BB0\u5F97\u5F7C\u6B64\uFF0C\u90A3\u4E48\u4FBF\u5728\u62E5\u6709\u7684\u65F6\u5019\uFF0C\u5C11\u4E00\u4E9B\u6307\u8D23\uFF0C\u591A\u4E00\u4E9B\u5BBD\u6170\uFF0C\u5C11\u4E00\u4E9B\u62B1\u6028\uFF0C\u591A\u4E00\u4E9B\u611F\u6069\u3002</p><p>\u4EBA\u751F\u6F2B\u6F2B\uFF0C\u4F60\u8D70\u8FC7\u7684\u8DEF\uFF0C\u8BFB\u8FC7\u7684\u4E66\uFF0C\u7231\u8FC7\u7684\u4EBA\uFF0C\u8DEF\u8FC7\u7684\u98CE\u666F\uFF0C\u7ECF\u5386\u8FC7\u7684\u5F80\u4E8B\uFF0C\u90FD\u4F1A\u4F7F\u4F60\u6210\u4E3A\u66F4\u52A0\u6E29\u6DA6\u7684\u81EA\u5DF1\uFF0C\u4F7F\u4F60\u5728\u672A\u6765\uFF0C\u53EF\u4EE5\u66F4\u5766\u7136\u5730\u8D70\u4E0B\u53BB\u3002</p><p>\u4F59\u751F\uFF0C\u613F\u4F60\u60F3\u5F00\u3001\u770B\u5F00\u3001\u653E\u5F00\uFF0C\u81EA\u5728\u4E00\u4E9B\uFF0C\u4E14\u884C\u4E14\u73CD\u60DC\u3002</p>',37);function d(h,t){return r}var c=e(a,[["render",d]]);export{c as default};
