import{d as p}from"./app.147292e0.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="\u5B63\u7FA1\u6797-\u4EBA\u751F\u7684\u610F\u4E49\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u5B63\u7FA1\u6797-\u4EBA\u751F\u7684\u610F\u4E49\u662F\u4EC0\u4E48" aria-hidden="true">#</a> \u5B63\u7FA1\u6797\uFF1A\u4EBA\u751F\u7684\u610F\u4E49\u662F\u4EC0\u4E48</h1><p>\u5F53\u6211\u8FD8\u662F\u4E00\u4E2A\u9752\u5E74\u5927\u5B66\u751F\u7684\u65F6\u5019\uFF0C\u62A5\u520A\u4E0A\u66FE\u522E\u8D77\u4E00\u9635\u8BA8\u8BBA\u4EBA\u751F\u7684\u610F\u4E49\u4E0E\u4EF7\u503C\u7684\u5FAE\u98CE\uFF0C\u6587\u7AE0\u5199\u4E86\u4E00\u4E9B\uFF0C\u8BAE\u8BBA\u4E5F\u53D1\u8868\u4E86\u4E00\u901A\u3002\u6211\u770B\u8FC7\u4E00\u4E9B\u6587\u7AE0\uFF0C\u4F46\u81EA\u5DF1\u5E76\u6CA1\u6709\u53C2\u52A0\u8FDB\u53BB\u3002</p><p>\u539F\u56E0\u662F\uFF0C\u6709\u7684\u6587\u7AE0\u4E0D\u77E5\u6240\u4E91\uFF0C\u6211\u770B\u4E0D\u61C2\u3002\u66F4\u91CD\u8981\u7684\u662F\uFF0C\u6211\u8BA4\u4E3A\u8FD9\u79CD\u8BA8\u8BBA\u672C\u8EAB\u5C31\u65E0\u610F\u4E49\uFF0C\u65E0\u4EF7\u503C\uFF0C\u4E0D\u5982\u5B9E\u5B9E\u5728\u5728\u5730\u5E72\u51E0\u4EF6\u4E8B\u597D\u3002</p><p>\u65F6\u5149\u6D41\u901D\uFF0C\u4E00\u8F6C\u773C\uFF0C\u81EA\u5DF1\u5DF2\u7ECF\u5230\u4E86\u671B\u4E5D\u4E4B\u5E74\uFF0C\u6D3B\u5F97\u8FDC\u8FDC\u8D85\u8FC7\u4E86\u6211\u7684\u9884\u7B97\u3002\u6709\u4EBA\u8BA4\u4E3A\u957F\u5BFF\u662F\u798F\uFF0C\u6211\u770B\u4E5F\u4E0D\u5C3D\u7136\u3002\u4EBA\u6D3B\u5F97\u592A\u4E45\u4E86\uFF0C\u5BF9\u4EBA\u751F\u7684\u79CD\u79CD\u76F8\uFF0C\u4F17\u751F\u7684\u79CD\u79CD\u76F8\uFF0C\u770B\u5F97\u900F\u900F\u5F7B\u5F7B\uFF0C\u53CD\u800C\u9F13\u821E\u65F6\u5C11\uFF0C\u53F9\u606F\u65F6\u591A\u3002\u8FDC\u4E0D\u5982\u65E9\u4E00\u70B9\u79BB\u5F00\u4EBA\u4E16\u8FD9\u4E2A\u662F\u975E\u4E4B\u5730\uFF0C\u843D\u4E00\u4E2A\u8033\u6839\u6E05\u51C0\u3002</p><p>\u90A3\u4E48\uFF0C\u957F\u5BFF\u5C31\u4E00\u70B9\u597D\u5904\u90FD\u6CA1\u6709\u5417\uFF1F\u4E5F\u4E0D\u662F\u7684\u3002\u8FD9\u5BF9\u4E86\u89E3\u4EBA\u751F\u7684\u610F\u4E49\u4E0E\u4EF7\u503C\uFF0C\u4F1A\u6709\u4E00\u4E9B\u597D\u5904\u7684\u3002</p><p>\u6839\u636E\u6211\u4E2A\u4EBA\u7684\u89C2\u5BDF\uFF0C\u5BF9\u4E16\u754C\u4E0A\u7EDD\u5927\u591A\u6570\u4EBA\u6765\u8BF4\uFF0C\u4EBA\u751F\u4E00\u65E0\u610F\u4E49\uFF0C\u4E8C\u65E0\u4EF7\u503C\u3002\u4ED6\u4EEC\u4E5F\u4ECE\u6765\u4E0D\u8003\u8651\u8FD9\u6837\u7684\u54F2\u5B66\u95EE\u9898\u3002</p><p>\u8D70\u8FD0\u65F6\uFF0C\u624B\u91CC\u6525\u6EE1\u4E86\u949E\u7968\uFF0C\u767D\u5929\u4E24\u987F\u7F8E\u98DF\u57CE\uFF0C\u665A\u4E0A\u4E00\u8D9F\u5361\u62C9 0K\uFF0C\u73A9\u4E00\u70B9\u5C0F\u6743\u672F\uFF0C\u800D\u4E00\u70B9\u5C0F\u806A\u660E\uFF0C\u751A\u81F3\u6063\u7762\u9A84\u6A2A\uFF0C\u98DE\u626C\u8DCB\u6248\uFF0C\u660F\u660F\u6C89\u6C89\uFF0C\u6D51\u6D51\u5669\u5669\uFF0C\u7B49\u5230\u94BB\u5165\u4E86\u9AA8\u7070\u76D2\uFF0C\u4E5F\u4E0D\u660E\u767D\u81EA\u5DF1\u4E3A\u4EC0\u4E48\u6D3B\u8FC7\u4E00\u751F\u3002</p><p>\u5176\u4E2D\u4E0D\u8D70\u8FD0\u7684\u5219\u7A77\u56F0\u6F66\u5012\uFF0C\u7EC8\u65E5\u4E3A\u8863\u98DF\u5954\u6CE2\uFF0C\u6101\u7709\u82E6\u8138\uFF0C\u957F\u5401\u77ED\u53F9\u3002\u5373\u4F7F\u65E5\u5B50\u8FD8\u80FD\u8FC7\u5F97\u53BB\u7684\uFF0C\u4E0D\u6101\u8863\u98DF\uFF0C\u80FD\u591F\u6E29\u9971\uFF0C\u7136\u800C\u4E5F\u7EC8\u65E5\u5FD9\u5FD9\u788C\u788C\uFF0C\u88AB\u56F0\u4E8E\u540D\u7F30\uFF0C\u88AB\u7F1A\u4E8E\u5229\u7D22\u3002\u540C\u6837\u662F\u660F\u660F\u6C89\u6C89\uFF0C\u6D51\u6D51\u5669\u5669\uFF0C\u4E0D\u77E5\u9053\u4E3A\u4EC0\u4E48\u6D3B\u8FC7\u4E00\u751F\u3002</p><p>\u5BF9\u8FD9\u6837\u7684\u82B8\u82B8\u4F17\u751F\uFF0C\u4EBA\u751F\u7684\u610F\u4E49\u4E0E\u4EF7\u503C\u4ECE\u4F55\u5904\u8C08\u8D77\u5462\uFF1F</p><p>\u6211\u81EA\u5DF1\u4E5F\u5C5E\u4E8E\u82B8\u82B8\u4F17\u751F\u4E4B\u5217\uFF0C\u4E5F\u96BE\u514D\u6D51\u6D51\u5669\u5669\uFF0C\u5E76\u4E0D\u6BD4\u4EFB\u4F55\u4EBA\u9AD8\u4E00\u4E1D\u4E00\u6BEB\u3002\u5982\u679C\u60F3\u52C9\u5F3A\u627E\u4E00\u70B9\u533A\u522B\u7684\u8BDD\uFF0C\u90A3\u4E5F\u662F\u6709\u7684\uFF1A\u6211\uFF0C\u5F53\u7136\u8FD8\u6709\u4E00\u4E9B\u522B\u7684\u4EBA\uFF0C\u5BF9\u4EBA\u751F\u6709\u4E00\u4E9B\u60F3\u6CD5\uFF0C\u52A8\u8FC7\u4E00\u70B9\u8111\u7B4B\uFF0C\u800C\u4E14\u81EA\u8BA4\u8FD9\u4E9B\u60F3\u6CD5\u662F\u6709\u70B9\u9053\u7406\u7684\u3002</p><p>\u6211\u6709\u4E9B\u4EC0\u4E48\u60F3\u6CD5\u5462\uFF1F\u8BDD\u8981\u8BF4\u5F97\u8FDC\u4E00\u70B9\u3002</p><p>\u5F53\u4ECA\u4E16\u754C\u4E0A\u6218\u706B\u7EB7\u98DE\uFF0C\u4EBA\u6B32\u6A2A\u6D41\uFF0C\u201C\u9EC4\u949F\u6BC1\u5F03\uFF0C\u74E6\u91DC\u96F7\u9E23\u201D\uFF0C\u662F\u4E00\u4E2A\u5341\u5206\u4E0D\u5B89\u5B9A\u7684\u65F6\u4EE3\u3002\u4F46\u662F\uFF0C\u5BF9\u4E8E\u4EBA\u7C7B\u7684\u524D\u9014\uFF0C\u6211\u59CB\u7EC8\u662F\u4E00\u4E2A\u4E50\u89C2\u4E3B\u4E49\u8005\u3002\u6211\u76F8\u4FE1\uFF0C\u4E0D\u7BA1\u8FD8\u8981\u7ECF\u8FC7\u591A\u5C11\u8270\u96BE\u66F2\u6298\uFF0C\u4E0D\u7BA1\u8FD8\u8981\u7ECF\u5386\u591A\u5C11\u65F6\u95F4\uFF0C\u4EBA\u7C7B\u603B\u4F1A\u8D8A\u53D8\u8D8A\u597D\u7684\uFF0C\u4EBA\u7C7B\u5927\u540C\u4E4B\u57DF\u51B3\u4E0D\u4F1A\u4EC5\u4EC5\u662F\u4E00\u4E2A\u7A7A\u6D1E\u7684\u7406\u60F3\u3002</p><p>\u4F46\u662F\uFF0C\u60F3\u8981\u8FBE\u5230\u8FD9\u4E2A\u76EE\u7684\uFF0C\u5FC5\u987B\u7ECF\u8FC7\u65E0\u6570\u4EE3\u4EBA\u7684\u5171\u540C\u52AA\u529B\u3002\u6709\u5982\u63A5\u529B\u8D5B\uFF0C\u6BCF\u4E00\u4EE3\u4EBA\u90FD\u6709\u81EA\u5DF1\u7684\u4E00\u6BB5\u8DEF\u7A0B\u8981\u8DD1\u3002\u53C8\u5982\u4E00\u6761\u94FE\u5B50\uFF0C\u662F\u7531\u8BB8\u591A\u73AF\u7EC4\u6210\u7684\uFF0C\u6BCF\u4E00\u73AF\u4ECE\u672C\u8EAB\u6765\u770B\uFF0C\u53EA\u4E0D\u8FC7\u662F\u5FAE\u4E0D\u8DB3\u9053\u7684\u4E00\u70B9\u4E1C\u897F\uFF1B\u4F46\u662F\u6CA1\u6709\u8FD9\u4E00\u70B9\u4E1C\u897F\uFF0C\u94FE\u5B50\u5C31\u7EC4\u4E0D\u6210\u3002</p><p>\u5728\u4EBA\u7C7B\u793E\u4F1A\u53D1\u5C55\u7684\u957F\u6CB3\u4E2D\uFF0C\u6211\u4EEC\u6BCF\u4E00\u4EE3\u4EBA\u90FD\u6709\u81EA\u5DF1\u7684\u4EFB\u52A1\uFF0C\u800C\u4E14\u662F\u7EDD\u975E\u53EF\u6709\u53EF\u65E0\u7684\u3002\u5982\u679C\u8BF4\u4EBA\u751F\u6709\u610F\u4E49\u4E0E\u4EF7\u503C\u7684\u8BDD\uFF0C\u5176\u610F\u4E49\u4E0E\u4EF7\u503C\u5C31\u5728\u8FD9\u91CC\u3002</p><p>\u4F46\u662F\uFF0C\u8FD9\u4E2A\u9053\u7406\u5728\u4EBA\u7C7B\u793E\u4F1A\u4E2D\u53EA\u6709\u5C11\u6570\u6709\u8BC6\u4E4B\u58EB\u624D\u80FD\u7406\u89E3\u3002</p><p>\u9C81\u8FC5\u5148\u751F\u6240\u79F0\u4E4B\u201C\u4E2D\u56FD\u7684\u810A\u6881\u201D\uFF0C\u6307\u7684\u5C31\u662F\u8FD9\u79CD\u4EBA\u3002\u5BF9\u4E8E\u90A3\u4E9B\u809A\u5B50\u91CC\u5403\u6EE1\u4E86\u80AF\u5FB7\u57FA\u3001\u9EA6\u5F53\u52B3\u3001\u6BD4\u8428\u997C\uFF0C\u5230\u5934\u6765\u7EC8\u4E0D\u8FC7\u662F\u6D51\u6D51\u5669\u5669\u7684\u4EBA\u6765\u8BF4\uFF0C\u6709\u5982\u590F\u866B\u4E0D\u8DB3\u4EE5\u4E0E\u8BED\u51B0\uFF0C\u8FD9\u4E9B\u9053\u7406\u662F\u6CA1\u6CD5\u8C08\u7684\u3002\u4ED6\u4EEC\u65E0\u6CD5\u7406\u89E3\u81EA\u5DF1\u5BF9\u4EBA\u7C7B\u53D1\u5C55\u6240\u5E94\u5F53\u627F\u62C5\u7684\u8D23\u4EFB\u3002</p><p>\u8BDD\u8BF4\u5230\u8FD9\u91CC\uFF0C\u6211\u60F3\u628A\u4E0A\u9762\u8BF4\u7684\u610F\u601D\u7B80\u77ED\u627C\u8981\u5730\u5F52\u7EB3\u4E00\u4E0B\uFF1A\u5982\u679C\u4EBA\u751F\u771F\u6709\u610F\u4E49\u4E0E\u4EF7\u503C\u7684\u8BDD\uFF0C\u5176\u610F\u4E49\u4E0E\u4EF7\u503C\u5C31\u5728\u4E8E\u5BF9\u4EBA\u7C7B\u53D1\u5C55\u7684\u627F\u4E0A\u542F\u4E0B\uFF0C\u627F\u524D\u542F\u540E\u7684\u8D23\u4EFB\u611F\u3002</p>',17);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
